sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {
    // current client script parameter
    const cspListQuery = api.state.listQuery;
    const cspListTitle = api.state.listTitle;

    const cspListQueryArray = cspListQuery.split('^u_request_type=');
    const cspListTitleArray = cspListTitle.split(' - ');

    // handled Event「update list query and title」で渡される値を取得
    const passedQuery = event.payload.heListQuery;
    const passedTitle = event.payload.heListTitle;

    if (passedQuery == 'legal' || passedQuery == 'facilities' || passedQuery == 'hr') {
        // ラジオボタンでall以外を選択したとき
        api.setState('listQuery', cspListQueryArray[0] + '^u_request_type=' + passedQuery);
        api.setState('listTitle', cspListTitleArray[0] + ' - ' + passedTitle);
        api.setState('reqType', passedQuery);
    } else if (passedQuery == 'all') {
        // ラジオボタンでallを選択したとき
        api.setState('listQuery', cspListQueryArray[0]);
        api.setState('listTitle', cspListTitleArray[0]);
        api.setState('reqType', 'all');
    } else {
       // ボタンをクリックしたとき
        if (api.state.reqType != 'all') {
            passedQuery = passedQuery + '^u_request_type=' + api.state.reqType;
            passedTitle = passedTitle + ' - ' + api.state.reqType;
        }
        api.setState('listQuery', passedQuery);
        api.setState('listTitle', passedTitle);
    }
}
